class Solution {
public:
    int removeDuplicates(vector<int>& nums) {
        if(nums.empty()){
            return 0;
        }
        int index = 0;
        for(int i = 1; i < nums.size(); i++)
        {
            if(nums[i] != nums[index])
                nums[++index] = nums[i];
        }
        return index+1;
    }
};

java:
public class Solution {
    public int removeDuplicates(int[] nums) {
        if (nums.length == 0) return 0;
        
        int size = 0;
        for (int i = 0; i < nums.length; i++) {
            if (nums[i] != nums[size]) {
                nums[++size] = nums[i];
            }
        }
        return size+1;
    }
}
